$(document).ready(function () {
    $('a[href^="#"]').on('click', function (e) {
        e.preventDefault();

        var target = this.hash,
            $target = $(target);

        $('html, body').stop().animate({
            'scrollTop': $target.offset().top - 100 // - 100px (nav-height)
        }, 1500, 'easeInOutExpo', function () {
            window.location.hash = '1' + target;
        });
    });
});
